import React from 'react';
import ReactDOM from 'react-dom';
import logo from './logo.svg';
import './App.css';
import {data} from './Datastore.js'
import { unstable_batchedUpdates } from 'react-dom';

function App() {

  function App() {
    const [searchTerm, setSearchTerm] = React.useState("");
    const [searchResults, setSearchResults] = React.useState([]);
    const handleChange = e => {
      setSearchTerm(e.target.value);
    };
    React.useEffect(() => {
      const results = data.filter(data =>
        data.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setSearchResults(results);
    }, [searchTerm]);
    return (
      <div className="App">
        <input
          type="text"
          placeholder="Search"
          value={searchTerm}
          onChange={handleChange}
        />
        <ul>
          {searchResults.map((key, value) => (
            <li class="lirender">  <a class="Linktext"href={key.Direct}> {key.Pagename} </a> </li>
          ))}
        </ul>
      </div>
    );
  }

  const rootElement = document.getElementById("Rendered");
  ReactDOM.render(<App />, rootElement);

  return (


(<div>

<div class="Panel1"> 

  <input type="text" placeholder="Search Anime" id="DirectorySearch" class="Searchbar" onChange={handleChange} value={searchTerm} ></input>

   </div>


</div>)
  );
}



