import ReactDOM from 'react-dom';
import logo from './logo.svg';
import './App.css';
import {data} from './Datastore.js'
import { unstable_batchedUpdates } from 'react-dom';

function App() {

  const Searchterm = React.useState("");
  const SearchResults = React.useState([]);

  const handleChange = e => {

    SearchTerm(e.Target.value);

  };

  React.useEffect(() => {

  const results = data.filter(data =>

    data.toLowerCase().includes(Searchterm.toLowerCase())

    );
  SearchResults(results);
  });



  return (


(<div>

<div class="Panel1"> 

  <input type="text" placeholder="Search Anime" id="DirectorySearch" class="Searchbar" ></input>

   </div>


</div>)
  );
}

const RenderList = SearchResults.map((key,value) =>

<li class="lirender">  <a class="Linktext"href={key.Direct}> {key.Pagename} </a> </li>

    );

  ReactDOM.render(

  <ul> {RenderList} </ul>,
  document.getElementById('Rendered')

  );






export default App;
